import React, { useState } from "react";
import "./App.css";
import whiteArrow from './assets/whitearrow.png';
import './colors.css';

function App() {
  const [phoneNumber, setPhoneNumber] = useState("");

  // Handle phone number change
  const handlePhoneChange = (e) => {
    setPhoneNumber(e.target.value);
  };

  // Validate phone number and send it to the serverless function
  const handleClick = async () => {
    if (validatePhoneNumber(phoneNumber)) {
      try {
        const response = await fetch('/api/addPhoneNumber', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ phoneNumber: phoneNumber }),
        });

        if (response.ok) {
          console.log("Phone number added to sheet");
        } else {
          console.error("Failed to add phone number");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    } else {
      console.log("Invalid phone number");
    }
  };

  // Basic phone number validation
  const validatePhoneNumber = (num) => {
    // Regex for basic phone number validation
    const regex = /^[0-9]{10}$/;
    return regex.test(num);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="content">
          <p>Hi, I'm Damon. Can I get your number?</p>
          <input
            type="tel"
            placeholder="Phone Number"
            className="phone-input"
            value={phoneNumber}
            onChange={handlePhoneChange}
          />
          <button onClick={handleClick} className="rsvpButton">
            <div className="rsvpButtonText">JOIN WAITLIST</div>
            <img className="arrow" alt="arrow" style={{ width: '30px', height: '24px' }} src={whiteArrow} />
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
