import React, { useState } from "react";
import "./App.css";
import whiteArrow from './assets/whitearrow.png';
import './colors.css';

function App() {
  const [phoneNumber, setPhoneNumber] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handlePhoneChange = (e) => {
    const input = e.target.value.replace(/\D/g, ''); // Remove non-digit characters
    let formattedPhoneNumber = '';

    for (let i = 0; i < input.length; i++) {
      if (i === 3 || i === 6) {
        formattedPhoneNumber += '-'; // Add a dash
      }
      formattedPhoneNumber += input[i];
    }

    setPhoneNumber(formattedPhoneNumber);
  };

  const handleClick = async () => {
    if (validatePhoneNumber(phoneNumber.replace(/-/g, ''))) {
      try {
        const response = await fetch('/api/addPhoneNumber', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ phoneNumber: phoneNumber.replace(/-/g, '') }),
        });

        if (response.ok) {
          console.log("Phone number added to sheet");
          setIsSubmitted(true);
        } else {
          console.error("Failed to add phone number");
        }
      } catch (error) {
        console.error("Error:", error);
      }
    } else {
      console.log("Invalid phone number");
    }
  };

  const validatePhoneNumber = (num) => {
    const regex = /^[0-9]{10}$/;
    return regex.test(num);
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="content">
          <div className="step">
            <div className="stepText">Hi, I'm Damon. Can I get your number?</div>
          </div>
          <input
            type="tel"
            placeholder="123-456-7890"
            className="inputStepText"
            value={phoneNumber}
            onChange={handlePhoneChange}
          />
          <button onClick={handleClick} className={`rsvpButton ${isSubmitted ? 'rsvpButtonSubmitted' : ''}`}>
            <div className="rsvpButtonText">{isSubmitted ? "You're in." : "JOIN WAITLIST"}</div>
            <img className="arrow" alt="arrow" style={{ width: '30px', height: '24px' }} src={whiteArrow} />
          </button>
        </div>
      </header>
    </div>
  );
}

export default App;
